INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(601, 81, '_image_2_droite', 'field_5308b1771be82'),
(602, 81, 'intro_2_droite', 'The \"organizational mutations orientation\" is concerned with change management in organizations and the emergence of new forms of organization: '),
(603, 81, '_intro_2_droite', 'field_5308b18b1be83'),
(604, 81, 'liste_2_droite', ' - What are the possible types of organizational arrangements? \r\n\r\n - What are the factors weighing on organizational choices? \r\n\r\n - What are the links between concrete work modes (division, coordination, etc.) and the other key variables (power distribution, objectives, contextual factors, etc.)? \r\n\r\n - How does one switch organizational types?  \r\n\r\n - How generate the minimum convergence needed between the project for change and the very transformation process?  \r\n\r\n - Etc. \r\n'),
(605, 81, '_liste_2_droite', 'field_5308b1aa1be84'),
(606, 81, 'titre_3_gauche', 'HRM mutations'),
(607, 81, '_titre_3_gauche', 'field_5308b1ea1be87'),
