INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(1881, 199, 'intro_2_droite', 'L’axe « mutations organisationnelles » a trait à la gestion du changement dans les organisations et à l’émergence de nouvelles formes organisationnelles:'),
(1882, 199, '_intro_2_droite', 'field_5308b18b1be83'),
(1883, 199, 'liste_2_droite', ' - Quels sont les différents types d’arrangements organisationnels possibles ?\r\n\r\n - Quels sont les facteurs qui pèsent dans les choix d’organisation ?\r\n\r\n - Quels liens y a-t-il entre modes concrets de travail (division, coordination, etc.) et les autres variables-clés (distribution du pouvoir, objectifs, facteurs de contexte, etc.) ?\r\n\r\n - Comment passer d’un type organisationnel à un autre ?\r\n\r\n - Comment créer le minimum de convergence nécessaire autour du projet de changement et du processus même de transformation ?\r\n\r\n - etc.'),
(1884, 199, '_liste_2_droite', 'field_5308b1aa1be84'),
(1885, 199, 'titre_3_gauche', 'Mutations en GRH'),
(1886, 199, '_titre_3_gauche', 'field_5308b1ea1be87'),
