INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(2069, 226, '_titre_1_gauche', 'field_5308b0a51be7d'),
(2070, 226, 'image_1_gauche', 223),
(2071, 226, '_image_1_gauche', 'field_5308b0e71be7e'),
(2072, 226, 'intro_1_gauche', 'The \"information and communication technology\" orientation led to the creation of the laboratory.  It centers around the organizational and human aspects of these technologies: '),
(2073, 226, '_intro_1_gauche', 'field_5308b0f41be7f'),
(2074, 226, 'liste_1_gauche', ' - What organizational assumptions underlie technological choices?\r\n\r\n - How do contexts influence the technologies brought into them? \r\n\r\n - How is the introduction of these technologies managed in terms of change management?  \r\n\r\n - What direct and indirect effects of introducing technologies can be observed?  \r\n\r\n - etc. \r\n'),
(2075, 226, '_liste_1_gauche', 'field_5308b13f1be80'),
(2076, 226, 'titre_2_droite', 'Organizational mutations orientation'),
(2077, 226, '_titre_2_droite', 'field_5308b15f1be81'),
