(2088, 226, 'intro_3_gauche', 'The \"HRM mutations\" orientation refers to the transformation of HRM practices within organizations: ');
INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(2089, 226, '_intro_3_gauche', 'field_5308b20f1be89'),
(2090, 226, 'liste_3_gauche', ' - What are the possible conventions regarding this?  \r\n\r\n - How does one deal with recent innovations (e-HRM, competence management, talent management, etc.)? \r\n\r\n - What are the links between HRM conventions and the various forms of organization?\r\n\r\n - Etc. '),
(2091, 226, '_liste_3_gauche', 'field_5308b2211be8a'),
(2092, 226, 'titre_4_droite', 'Labour market mutations'),
