INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(2169, 229, 'titre_1_gauche', 'Technologies de l’information et de la communication'),
(2170, 229, '_titre_1_gauche', 'field_5308b0a51be7d'),
(2171, 229, 'image_1_gauche', 223),
(2172, 229, '_image_1_gauche', 'field_5308b0e71be7e'),
(2173, 229, 'intro_1_gauche', 'L’axe « technologies de l’information et de la communication » fut à l’origine de la création du laboratoire. Il est centré sur les aspects organisationnels et humains de ces technologies :'),
(2174, 229, '_intro_1_gauche', 'field_5308b0f41be7f'),
(2175, 229, 'liste_1_gauche', '- Quels sont les présupposés organisationnels inscrits dans les choix technologiques ?\r\n\r\n- Quelle est l’influence des contextes dans lesquelles les technologies sont introduites ?\r\n\r\n- Comment est menée l’implantation de ces technologies en termes de gestion du changement ?\r\n\r\n- Quels sont les effets directement et indirectement observables de l’introduction des technologies\r\n\r\n- etc.'),
(2176, 229, '_liste_1_gauche', 'field_5308b13f1be80'),
(2177, 229, 'titre_2_droite', 'Mutations organisationnelles'),
(2178, 229, '_titre_2_droite', 'field_5308b15f1be81');
INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(2179, 229, 'image_2_droite', 222),
(2180, 229, '_image_2_droite', 'field_5308b1771be82'),
(2181, 229, 'intro_2_droite', 'L’axe « mutations organisationnelles » a trait à la gestion du changement dans les organisations et à l’émergence de nouvelles formes organisationnelles:'),
(2182, 229, '_intro_2_droite', 'field_5308b18b1be83'),
(2183, 229, 'liste_2_droite', '- Quels sont les différents types d’arrangements organisationnels possibles ?\r\n\r\n- Quels sont les facteurs qui pèsent dans les choix d’organisation ?\r\n\r\n- Quels liens y a-t-il entre modes concrets de travail (division, coordination, etc.) et les autres variables-clés (distribution du pouvoir, objectifs, facteurs de contexte, etc.) ?\r\n\r\n- Comment passer d’un type organisationnel à un autre ?\r\n\r\n- Comment créer le minimum de convergence nécessaire autour du projet de changement et du processus même de transformation ?\r\n\r\n- etc.'),
(2184, 229, '_liste_2_droite', 'field_5308b1aa1be84'),
(2185, 229, 'titre_3_gauche', 'Mutations en GRH'),
(2186, 229, '_titre_3_gauche', 'field_5308b1ea1be87'),
(2187, 229, 'image_3_gauche', 224),
(2188, 229, '_image_3_gauche', 'field_5308b1fe1be88');
INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(2189, 229, 'intro_3_gauche', 'L’axe « mutations en GRH » se réfère à la transformation des pratiques de GRH dans les organisations :'),
(2190, 229, '_intro_3_gauche', 'field_5308b20f1be89'),
(2191, 229, 'liste_3_gauche', '- Quels sont les différents modèles possibles à cet égard ?\r\n\r\n- Comment appréhender les innovations récentes (e-GRH, gestion des compétences, etc.)?\r\n\r\n- Quels liens les modèles de GRH entretiennent-ils avec les différentes formes organisationnelles ?\r\n\r\n- Quel rôle la GRH peut-elle jouer dans la transformation effective des organisations ?\r\n\r\n- etc.'),
(2192, 229, '_liste_3_gauche', 'field_5308b2211be8a'),
(2193, 229, 'titre_4_droite', 'Mutations du marché du travail'),
(2194, 229, '_titre_4_droite', 'field_5308b2361be8b'),
